def admissionFaqTool(query):
    faq_data = [
        {
            "question": "What is the eligibility for B.Tech?",
            "answer": "You must have passed 12th standard with Physics, Chemistry, and Mathematics with at least 50% marks."
        },
        {
            "question": "What is the last date for admission?",
            "answer": "The last date for admission is August 30, 2025."
        },
        {
            "question": "What is the fee structure for courses?",
            "answer": "The fee ranges from INR 50,000 to INR 1,20,000 depending on the course."
        },
        {
            "question": "How can I apply for admission?",
            "answer": "You can apply through the college website or visit the admission office directly."
        },
        {
            "question": "What are the available courses?",
            "answer": "We offer B.Tech, BBA, B.Com, and MBA programs."
        },
        {
            "question": "Is there a hostel facility available?",
            "answer": "Yes, hostel facilities are available for both boys and girls."
        },
        {
            "question": "Is there any entrance exam required?",
            "answer": "Yes, students must qualify a state or national level entrance exam depending on the program."
        },
        {
            "question": "What is the eligibility for MBA?",
            "answer": "Candidates must have a bachelor's degree with at least 50% marks. Some programs may require entrance exams like CAT or MAT."
        },
        {
            "question": "What is the fee for MBA program?",
            "answer": "The MBA program fee is INR 1,20,000 per year."
        },
        {
            "question": "Are there any scholarships available?",
            "answer": "Yes, we offer merit-based and need-based scholarships. Please contact the scholarship office for more details."
        },
        {
            "question": "What documents are needed for admission?",
            "answer": "You need your 10th and 12th marksheets, ID proof, entrance exam rank card, and passport-size photographs."
        },
        {
            "question": "How can I contact the admission office?",
            "answer": "You can call +91-9876543210 or email admissions@college.edu."
        }
    ]

    query_lower = query.lower()

    for item in faq_data:
        if item["question"].lower() in query_lower or query_lower in item["question"].lower():
            return item["answer"]

    return "Sorry, I couldn't find an answer. Please contact the admission office."
